(2066, 246, 'our_story_item_image', '155'),
(2067, 246, '_our_story_item_image', 'field_5f8c448954e98'),
(2072, 248, 'our_story_item_title', 'Современные технологии'),
(2073, 248, '_our_story_item_title', 'field_5f8c4489546c8'),
